import React, { useState } from "react";

import Search from "./Search";
import NewsList from "./NewsList";
import Header from "./Header";

import axios from "axios";

const NewsPage = () => {
  const [news, setNews] = useState([]);
  const [searchText, setSearchText] = useState("");

  const handleChangeSearchInput = e => {
    const { value, name } = e.target;
    setSearchText(value);
  };

  const handleSearch = searchText => {
    const getData = searchText => {
      axios
        .get(
          `https://newsapi.org/v2/everything?q=${searchText}&from=2021-04-26&sortBy=publishedAt&apiKey=f04510568ac84dff9c52853369a2c7fd`
        )
        .then(response => {
          let resultNewsList = response.data.articles;
          setNews(resultNewsList);
        });
    };
  };

  return (
    <div>
      <Header title="뉴스 검색" />
      <Search
        handleChangeSearchInput={handleChangeSearchInput}
        handleClickSearchButton={handleSearch}
      />
      <NewsList news={news} />
    </div>
  );
};

export default NewsPage;
